import { Component } from '@angular/core';

@Component({
  selector: 'app-books',
  templateUrl: './books.component.html',
  styleUrls: ['./books.component.css']
})
export class BooksComponent {
name:string = "Clean code";
author:string="Robert C";
img_src:string="https://m.media-amazon.com/images/W/WEBP_402378-T2/images/I/71UoqmNAv8L._AC_UY218_.jpg"
isDisabled:boolean=false
handleClick(){
console.log("hello")
this.isDisabled=true
}
iname :string="";
handleInput(event:any){
  this.iname= event.target.value
}
}
<h2>Programming books list!</h2>
<button (click)="handleClick()" [disabled]="isDisabled">show button</button>
<input type="text" (input)="handleInput($event)">
<input type="text" [(input)]="iname">
<p>{{iname}}</p>
<ul>
    <li><img src="{{img_src}}" alt="{{name}}" width="100"/>
        {{ name }} by {{ author }}</li>

</ul>
