import { Component } from '@angular/core';

@Component({
  selector: 'app-books',
  templateUrl: './books.component.html',
  styleUrls: ['./books.component.css']
})
export class BooksComponent {
name:string = "Clean code";
author:string="Robert C";
img_src:string="https://m.media-amazon.com/images/W/WEBP_402378-T2/images/I/71UoqmNAv8L._AC_UY218_.jpg"
}
<h2>Programming books list!</h2>
<button [disabled]="isDisabled"></button>
<ul>
    <li><img src="{{img_src}}" alt="{{name}}" width="100"/>
        {{ name }} by {{ author }}</li>

</ul>
